import React from 'react';
import AddToPlaylistForm from '../Playlist/AddToPlaylistForm'
import styled from "styled-components";

class SearchedSongs extends React.Component {
  state={
    clicked: false
  }

  clickHandler = () => {
    this.setState({
      clicked: !this.state.clicked
    })
  }
  render(){
    return(

      <Card>
          <img src={this.props.track.image} alt="" />
          <span>{this.props.track.name}</span>
          <span>{this.props.track.artists}</span>
          <button onClick={this.clickHandler} type="button" className="btn btn-info">Add to My Playlist</button>
        {this.state.clicked ? (<AddToPlaylistForm playlist={this.props.playlist} track={this.props.track} addToPlaylist={this.props.addToPlaylist}/>) : (null)}
        <p>Preview:</p>
        {this.props.track.preview !== null ?
          <audio
            ref="audio_tag"
            src={this.props.track.preview}
            controls
          /> :
            <p>No preview available</p>
        }
  </Card>
    )
  }
}
export default SearchedSongs;


const Card = styled.div`
    display: block;
    background-color: #303859;
    width: 400px;
    height: 350px;
    padding: 15px;
    margin: 15px;
    box-shadow: 4px 4px 8px 0px rgb(0,0,0,0.2);
    border-radius: 5px;
    a{
        text-decoration: none;
    }
    img{
        width: 100%;
    }
    .title{
        float: left;
        color: white;
        font-size: 1.1rem;
        padding: 5px;
        font-family: 'Lemonada', cursive;
    }
    .genre{
        float: right;
        color: white;
        padding: 5px;
        font-size: 0.95rem;
        font-family: 'Lemonada', cursive;
    }
    .desc {
        float: left;
        width: fit-content;
        font-size: 1.5rem;
        color: white;
        font-family: 'Lemonada', cursive;
        padding: 5px;
        text-align: left
    }
    .platform {
        float: left;
        align-self: flex-end;
        padding: 5px
    }
`
