import React from 'react';
import Search from '../Search/Search'
import SearchedSongs from '../Search/SearchedSongs'
import Loading from '../Login/Loading'
// import AddToPlaylistForm from './AddToPlaylistForm'
import {Link} from 'react-router-dom'

class Home extends React.Component{

  render(){
    return(
      <div id="home-container">
                <video id="home-video" autoPlay loop muted src={Video} type='video/mp4'>
               </video>

           <div id='title-container' style={{marginTop: '-10px'}}>

                <h1 id='home-title' style={{paddingLeft: '137px'}}>Christmas Spotify</h1>
           </div>
      <div className="home">
      <h1>Home</h1>

      {this.props.login ? (
        <div className="container">
        <h2>Search For Your Favorite Song:</h2>
        <Search fetchSearchedSongs={this.props.fetchSearchedSongs} />
        {this.props.searchedSongs.length === 0 && this.props.loading ? (<Loading />) :
        this.props.searchedSongs.map((track, i) => (
          <SearchedSongs
            key={i}
            track={track}
            addToPlaylist={this.props.addToPlaylist}
            playlist={this.props.playlist}
          />
        ))}
      </div>
    ) : (
        <h2>Welcome to Playlister! Please <Link to={"/login"} >Log in</Link> or <Link to={"/signup"}>Register</Link></h2>
    )
  }

      </div>
      </div>
    )
  }
}
export default Home;
